mapper.update:
UPDATE APPROVAL
   SET APPR_STS = #{apprSts}
	 , APPR_FNSH_DT = SYSDATE
 WHERE APPR_ID = #{apprId}

  		UPDATE APPROVAL
		   SET DEL_YN='Y'
		 WHERE APPR_ID= #{_parameter}

mapper.select:
SELECT COUNT(*) FROM APPROVAL INNER JOIN EMPLOYEE e ON e.EMP_ID=#{empId}
AND APPR_TTL LIKE '%' || #{searchKeyword} || '%'
AND e.EMP_ID LIKE '%' || #{searchKeyword} || '%'

SELECT A.APPR_ID , TO_CHAR(A.DMD_DT, 'YYYY-MM-DD HH24:MM') DMD_DT , A.APPR_CTGR , A.APPR_STS , A.APPR_TTL , A.APPR_CNTNT , A.DMD_ID , A.APPR_MNG_ID , A.ARRP_RJCT , A.LAST_CN_DT , A.APPR_FNSH_DT , E.EMP_NAME , CC.CMCD_NAME FROM APPROVAL A INNER JOIN EMPLOYEE E ON A.DMD_ID = E.EMP_ID INNER JOIN COMMON_CODE CC ON E.PSTN_ID = CC.CMCD_ID WHERE A.DMD_ID = #{empId} AND A.DEL_YN = 'N'
AND a.APPR_TTL = #{searchKeyword}
AND e.EMP_ID = #{searchKeyword}
