mapper.update:
UPDATE BORROW_HISTORY 
   SET RTN_DT = SYSDATE 
 WHERE BRRW_HIST_ID = #{_parameter}

mapper.insert:
INSERT INTO BORROW_HISTORY ( BRRW_HIST_ID , BRRW_ID , PRDT_MNG_ID , BRRW_DT , RTN_DT , DEL_YN) SELECT 'BRRW_HT' || TO_CHAR(SYSDATE, 'YYMMDD') || '_' || LPAD(SEQ_BRRW_HT_PK.NEXTVAL, 6, '0'), BH.* FROM (
SELECT #{borrowVO.brrwId} AS BRRW_ID , #{borrowVO.prdtMngId} AS PRDT_MNG_ID , SYSDATE AS BRRW_DT , NULL AS RTN_DT , 'N' AS DEL_YN FROM DUAL
) BH

INSERT INTO BORROW_HISTORY
	 ( BRRW_HIST_ID
     , BRRW_ID
     , PRDT_MNG_ID
     , BRRW_DT
     , RTN_DT
     , DEL_YN)
	VALUES
	 (#{brrwHistId}
	, #{brrwId}
	, #{prdtMngId}
	, #{brrwDt}
	, #{rtnDt}
	, 'N')
