mapper.select:
SELECT BH.BRRW_HIST_ID , BH.BRRW_ID , BH.PRDT_MNG_ID , TO_CHAR(BH.BRRW_DT, 'YYYY-MM-DD') BRRW_DT , TO_CHAR(BH.RTN_DT, 'YYYY-MM-DD') RTN_DT , BH.DEL_YN , P.PRDT_NAME , P.ONCE_YN , P.PRDT_CTGR FROM BORROW_HISTORY BH INNER JOIN PRODUCT_MANAGEMENT PM ON BH.PRDT_MNG_ID = PM.PRDT_MNG_ID INNER JOIN PRODUCT P ON P.PRDT_ID = PM.PRDT_ID WHERE BH.DEL_YN = 'N' AND BH.BRRW_ID = #{_parameter}
AND P.PRDT_NAME '%' || #{searchKeyword} || '%'
AND BH.PRDT_MNG_ID LIKE '%' || #{searchKeyword} || '%'

mapper.update:
UPDATE BORROW_HISTORY 
   SET RTN_DT = SYSDATE 
 WHERE BRRW_HIST_ID = #{_parameter}

mapper.insert:
INSERT INTO BORROW_HISTORY ( BRRW_HIST_ID , BRRW_ID , PRDT_MNG_ID , BRRW_DT , RTN_DT , DEL_YN) SELECT 'BRRW_HT' || TO_CHAR(SYSDATE, 'YYMMDD') || '_' || LPAD(SEQ_BRRW_HT_PK.NEXTVAL, 6, '0'), BH.* FROM (
SELECT #{borrowVO.brrwId} AS BRRW_ID , #{borrowVO.prdtMngId} AS PRDT_MNG_ID , SYSDATE AS BRRW_DT , NULL AS RTN_DT , 'N' AS DEL_YN FROM DUAL
) BH
