mapper.update:
UPDATE EMPLOYEE
   SET EMP_NAME = #{empName}
	 , WORK_STS = #{workSts}
	 , SAL_YEAR = #{salYear}

	 , PRFL = #{prfl}

	 , HIRE_YEAR =# {hireYear}
	 , END_DT  =# {emdDt}
	 , REST_ST_DT = #{restStDt}
	 , REST_END_DT = #{restEndDt}
	 , CNTCT = #{cntct}
	 , ADDR = #{addr}
	 , BRTH = #{brth}
	 , EMAIL = #{email}
	 , MNGR_YN = #{mngrYn}
	 , ADMN_CODE = #{admnCode}
	 , DEPT_ID = #{deptId}
	 , JOB_ID = #{jobId}
	 , PSTN_ID = #{pstnId}
 WHERE EMP_ID = #{empId}

UPDATE EMPLOYEE
   SET EMP_NAME = #{empName}
	 , WORK_STS = #{workSts}
	 , HIRE_YEAR = #{hireYear}
	 , HIRE_DT = #{hireDt}
	 , DEPT_ID = #{deptId}
 WHERE EMP_ID = #{empId}

		UPDATE EMPLOYEE 
   		   SET DEL_YN = 'Y'
 		 WHERE EMP_ID = #{empId}

