mapper.update:
UPDATE ISSUE
   SET IS_TTL = #{isTtl}
     , IS_CNTNT = #{isCntnt}
     , MDF_DT = SYSDATE

     , FILE_NAME = #{fileName}
     , ORIGIN_FILE_NAME = #{originFileName}

 WHERE IS_ID = #{isId}

UPDATE ISSUE
   SET DEL_YN = 'Y'
 WHERE IS_ID = #{isId}

mapper.select:
SELECT FILE_NAME FROM ISSUE WHERE IS_ID IN
#{isId}
AND DEL_YN = 'N'
