mapper.update:
UPDATE EMPLOYEE
SET LGN_YN = 'Y'
WHERE EMP_ID = #{empId}

UPDATE EMPLOYEE
SET LGN_YN = 'N'
WHERE EMP_ID = #{empId}

mapper.select:
    		SELECT LGN_TRY
			  FROM EMPLOYEE
			 WHERE EMP_ID = #{empId}

mapper.update:
    		UPDATE EMPLOYEE
			   SET LGN_TRY = LGN_TRY + 1
			 WHERE EMP_ID = #{empId}

    		UPDATE EMPLOYEE
			   SET LGN_TRY = 0
			 WHERE EMP_ID = #{empId}

mapper.select:
SELECT SALT
FROM EMPLOYEE
WHERE EMP_ID = #{_parameter}
