mapper.select:
SELECT LEVEL , O.OUT_ID , O.OUT_TYPE , O.OUT_TTL , O.OUT_VER , O.OUT_VER_NUM , O.OUT_FILE , O.OUT_ENCODE_FILE , TO_CHAR(O.CRT_DT, 'YYYY-MM-DD') AS CRT_DT , O.CRTR_ID , TO_CHAR(O.MDF_DT, 'YYYY-MM-DD') AS MDF_DT , O.MDFR_ID , O.PRJ_ID , O.DEL_YN , CC.CMCD_NAME AS OUTPUT_TYPE_NAME , OUTVERSTS.CMCD_NAME AS OUT_VER_STS_NAME , P.PRJ_NAME , EMP.EMP_NAME FROM PMS."OUTPUT" O JOIN COMMON_CODE CC ON O.OUT_TYPE = CC.CMCD_ID JOIN PROJECT P ON O.PRJ_ID = P.PRJ_ID JOIN COMMON_CODE OUTVERSTS ON O.OUT_VER = OUTVERSTS.CMCD_ID JOIN EMPLOYEE EMP ON O.CRTR_ID = EMP.EMP_ID
WHERE O.DEL_YN='N' AND P.OUT_YN='Y'
AND O.PRJ_ID = #{prjId}
AND O.PRJ_ID IN (SELECT PRJ_ID FROM PROJECT_TEAMMATE WHERE TM_ID = #{empId} )
AND O.OUT_TYPE = #{outType}
AND O.OUT_VER = #{outVer}
START WITH O.OUT_VER_NUM IS NULL CONNECT BY PRIOR O.OUT_ID = O.OUT_VER_NUM ORDER BY O.OUT_ID DESC

SELECT COUNT(1) FROM PMS."OUTPUT" O JOIN COMMON_CODE CC ON O.OUT_TYPE = CC.CMCD_ID JOIN PROJECT P ON O.PRJ_ID = P.PRJ_ID JOIN COMMON_CODE OUTVERSTS ON O.OUT_VER = OUTVERSTS.CMCD_ID JOIN EMPLOYEE EMP ON O.CRTR_ID = EMP.EMP_ID WHERE O.DEL_YN='N' AND P.OUT_YN='Y'
AND O.PRJ_ID = #{prjId}
AND O.PRJ_ID IN (SELECT PRJ_ID FROM PROJECT_TEAMMATE WHERE TM_ID = #{empId} )
AND O.OUT_TYPE = #{outType}
AND O.OUT_VER = #{outVer}

mapper.update:
UPDATE PMS."OUTPUT"
SET OUT_TYPE=#{outType}
, OUT_TTL=#{outTtl}
, OUT_VER=#{outVer}
, OUT_FILE=#{outFile}
, MDF_DT= SYSDATE
, MDFR_ID='00000'
, OUT_ENCODE_FILE=#{outEncodeFile}
, OUT_VER_NUM =#{outVerNum}
WHERE OUT_ID=#{outId}
