mapper.update:
UPDATE PRODUCT_MANAGEMENT 
   SET DEL_YN = 'Y'
 WHERE PRDT_MNG_ID = #{_parameter}

UPDATE PRODUCT_MANAGEMENT 
   SET PRDT_PRICE = #{prdtPrice}
	 , BUY_DT = #{buyDt}
	 , LOST_YN = #{lostYn}
	 , LOST_DT = #{lostDt}
 WHERE PRDT_MNG_ID = #{prdtMngId}

UPDATE PRODUCT_MANAGEMENT
   SET BRRW_YN = 'N'
 WHERE PRDT_MNG_ID = #{_parameter}

mapper.select:
SELECT PRDT_ID
  FROM PRODUCT_MANAGEMENT
 WHERE PRDT_MNG_ID = #{_parameter}
