mapper.update:
UPDATE PRODUCT 
   SET CUR_STR = CUR_STR - 1
 WHERE PRDT_ID = #{_parameter}

UPDATE PRODUCT 
   SET CUR_STR = CUR_STR + 1
 WHERE PRDT_ID = #{_parameter}

UPDATE PRODUCT 
   SET CUR_STR = CUR_STR + #{curStr}
 WHERE PRDT_ID = #{prdtId}

UPDATE PRODUCT 
   SET DEL_YN = 'Y'
 WHERE PRDT_ID = #{_parameter}

UPDATE PRODUCT 
   SET PRDT_NAME = #{prdtName}
     , PRDT_CTGR = #{prdtCtgr}
     , ONCE_YN = #{onceYn}
 WHERE PRDT_ID = #{prdtId}
