mapper.select:
SELECT  COUNT(1)
  FROM 	REQUIREMENT r  	
  JOIN	PROJECT p 
  	ON	r.PRJ_ID = p.PRJ_ID 
 WHERE	R.DEL_YN = 'N'	
   AND	P.REQ_YN= 'Y'

   AND	R.PRJ_ID=#{prjId}


   AND	R.SCD_STS=#{scdSts}


   AND	R.RQM_STS=#{rqmSts}

SELECT R.RQM_ID , R.RQM_TTL , R.RQM_CNTNT , R.SCD_STS , CC1.CMCD_NAME AS STS_NAME , R.RQM_STS , cc2.CMCD_NAME AS RQM_NAME , r.PRJ_ID , p.PRJ_NAME FROM REQUIREMENT r JOIN COMMON_CODE cc1 ON R.SCD_STS =CC1.CMCD_ID JOIN COMMON_CODE cc2 ON r.RQM_STS = cc2.CMCD_ID JOIN PROJECT p ON r.PRJ_ID = p.PRJ_ID WHERE R.DEL_YN = 'N' AND P.REQ_YN= 'Y'
AND R.PRJ_ID=#{prjId}
AND R.SCD_STS=#{scdSts}
AND R.RQM_STS=#{rqmSts}
ORDER BY R.RQM_ID DESC

mapper.update:
UPDATE PMS.REQUIREMENT SET 
  RQM_CNTNT=#{rqmCntnt}
, STRT_DT=#{strtDt}
, END_DT=#{endDt}
, RQM_FILE=#{rqmFile}
, RQM_ENCODE_FILE=#{rqmEncodeFile}
, SCD_STS=#{scdSts}
, RQM_STS=#{rqmSts}
, MDF_DT=SYSDATE
, RQM_TTL=#{rqmTtl}
WHERE RQM_ID=#{rqmId}

UPDATE PMS.REQUIREMENT SET 
  DEL_YN = 'Y'
WHERE RQM_ID=#{rqmId}

 UPDATE PMS.REQUIREMENT SET 
SCD_STS = '503'
  WHERE RQM_ID=#{rqmId}
