mapper.select:
SELECT COUNT(1) FROM REQUIREMENT R JOIN COMMON_CODE STS ON R.SCD_STS =STS.CMCD_ID JOIN COMMON_CODE RQM ON R.RQM_STS = RQM.CMCD_ID JOIN PROJECT p ON R.PRJ_ID = p.PRJ_ID JOIN EMPLOYEE DVLRP ON R.dvlrp = DVLRP.EMP_ID JOIN EMPLOYEE CFRMR ON R.CFRMR = CFRMR.EMP_ID JOIN EMPLOYEE TSTR ON R.TSTR = TSTR.EMP_ID JOIN EMPLOYEE CRTR ON R.CRTR_ID = CRTR.EMP_ID LEFT JOIN EMPLOYEE MDFR ON R.MDFR_ID = MDFR.EMP_ID WHERE R.DEL_YN = 'N' AND P.REQ_YN= 'Y'
AND R.PRJ_ID = #{prjId}
AND R.PRJ_ID IN (SELECT PRJ_ID FROM PROJECT_TEAMMATE WHERE TM_ID = #{empId} )
AND R.SCD_STS=#{scdSts}
AND R.RQM_STS=#{rqmSts}
ORDER BY R.RQM_ID DESC

SELECT R.RQM_ID , R.RQM_CNTNT , TO_CHAR(R.STRT_DT, 'YYYY-MM-DD') AS STRT_DT , TO_CHAR(R.END_DT, 'YYYY-MM-DD') AS END_DT , R.RQM_FILE , R.DVLRP , R.CFRMR , R.TSTR , R.TEST_RSLT , R.SCD_STS , R.RQM_STS , TO_CHAR(R.CRT_DT, 'YYYY-MM-DD') AS CRT_DT , TO_CHAR(R.MDF_DT, 'YYYY-MM-DD') AS MDF_DT , R.CRTR_ID , R.MDFR_ID , R.PRJ_ID , R.RQM_TTL , R.RQM_ENCODE_FILE , STS.CMCD_NAME AS STS_NAME , RQM.CMCD_NAME AS RQM_NAME , P.PRJ_NAME AS PRJ_NAME , DVLRP.EMP_NAME AS DVLRP_NAME , CFRMR.EMP_NAME AS CFRMR_NAME , TSTR.EMP_NAME AS TSTR_NAME , CRTR.EMP_NAME AS CRTR_NAME , MDFR.EMP_NAME AS MDFR_NAME FROM REQUIREMENT R JOIN COMMON_CODE STS ON R.SCD_STS =STS.CMCD_ID JOIN COMMON_CODE RQM ON R.RQM_STS = RQM.CMCD_ID JOIN PROJECT p ON R.PRJ_ID = p.PRJ_ID JOIN EMPLOYEE DVLRP ON R.dvlrp = DVLRP.EMP_ID JOIN EMPLOYEE CFRMR ON R.CFRMR = CFRMR.EMP_ID JOIN EMPLOYEE TSTR ON R.TSTR = TSTR.EMP_ID JOIN EMPLOYEE CRTR ON R.CRTR_ID = CRTR.EMP_ID LEFT JOIN EMPLOYEE MDFR ON R.MDFR_ID = MDFR.EMP_ID WHERE R.DEL_YN = 'N' AND P.REQ_YN= 'Y'
AND R.PRJ_ID = #{prjId}
AND R.PRJ_ID IN (SELECT PRJ_ID FROM PROJECT_TEAMMATE WHERE TM_ID = #{empId} )
AND R.SCD_STS=#{scdSts}
AND R.RQM_STS=#{rqmSts}
ORDER BY R.RQM_ID DESC

mapper.insert:
INSERT INTO PMS.REQUIREMENT
( RQM_ID
, RQM_TTL
, RQM_CNTNT
, STRT_DT
, END_DT
, RQM_FILE
, DVLRP
, CFRMR
, TSTR
, TEST_RSLT
, SCD_STS
, RQM_STS
, CRT_DT
, CRTR_ID
, MDF_DT
, MDFR_ID
, PRJ_ID
, DEL_YN
, RQM_ENCODE_FILE)
VALUES
( ('RQM_' || TO_CHAR(SYSDATE, 'YYMMDD') || '_' || LPAD(SEQ_RQM_PK.NEXTVAL, 6, '0'))
, #{rqmTtl}
, #{rqmCntnt}
, #{strtDt}
, #{endDt}
, #{rqmFile}
, #{cfrmr}
, #{tstr}
, #{dvlrp}
, ''
, #{scdSts}
, #{rqmSts}
, SYSDATE	
, #{crtrId}
, ''
, ''
, #{prjId}
, 'N'
, #{rqmEncodeFile} )

mapper.update:
UPDATE PMS.REQUIREMENT SET 
  RQM_CNTNT=#{rqmCntnt}
, STRT_DT=#{strtDt}
, END_DT=#{endDt}
, RQM_FILE=#{rqmFile}
, RQM_ENCODE_FILE=#{rqmEncodeFile}
, SCD_STS=#{scdSts}
, RQM_STS=#{rqmSts}
, MDF_DT=SYSDATE
, RQM_TTL=#{rqmTtl}
, DVLRP=#{dvlrp}
, CFRMR=#{cfrmr}
, TSTR=#{tstr}
, MDFR_ID=#{mdfrId}
WHERE RQM_ID=#{rqmId}

UPDATE PMS.REQUIREMENT SET 
  DEL_YN = 'Y'
WHERE RQM_ID=#{rqmId}

 UPDATE PMS.REQUIREMENT SET 
SCD_STS = '503'
  WHERE RQM_ID=#{rqmId}


UPDATE PMS.REQUIREMENT SET 
TEST_RSLT=#{testRslt}


,RQM_STS='605'
,SCD_STS='504'


,RQM_STS='603'


 WHERE RQM_ID=#{rqmId}
