mapper.update:
UPDATE SURVEY_QUESTION
   SET SRV_QST = #{srvQst}
     , TYPE_YN = #{typeYn}
     , SRV_STS = #{srvSts}
 WHERE SRV_ID = #{srvId}

UPDATE SURVEY_QUESTION
   SET SRV_QST = #{srvQst}
     , MDFR_ID = #{mdfrId}
     , MDF_DT = SYSDATE
     , SEQ = #{seq}
     , TYPE_YN = #{typeYn}
 WHERE SRV_ID = #{srvId}

UPDATE SURVEY_QUESTION
   SET SEQ = #{seq}
     , TYPE_YN = #{typeYn}
 WHERE SRV_ID = #{srvId}

UPDATE SURVEY_QUESTION
   SET MDFR_ID = #{mdfrId}
     , MDF_DT = SYSDATE
     , DEL_YN = 'Y'
 WHERE SRV_ID = #{srvId}
